INSERT INTO `questions` VALUES (1194, 'What does the article mention about Orange Bay Kitchen?', 'D', '<p><span style=\"color: #33CC33;\">191. Bài báo đề cập đến điều gì về Orange Bay Kitchen? </span><br>(A) Hiện tại đang thuê người phục vụ.<br>(B) Nó nằm trên một con phố yên tĩnh.<br>(C) Nó có một địa điểm khác ở Jamaica.<br><span style=\"color: #33CC33;\"> (D) Nó mở cửa sáu tháng trước. </span></p>', 'A. It is currently hiring servers.', 'B. It is located on a quiet street.', 'C. It has another location in Jamaica.', 'D. It opened six months ago.', '2025-04-28 23:45:18', '2025-05-02 01:00:50', 180);
INSERT INTO `questions` VALUES (1195, 'According to the article, what is the most popular menu item at Orange Bay Kitchen?', 'C', '<p><span style=\"color: red;\">192. Theo bài báo, món nào được yêu thích nhất trong thực đơn tại Orange Bay Kitchen? </span><br>(A) Cá hồng<br>(B) Súp đuôi bò<br><span style=\"color: red;\"> (C) Gà nướng</span><br>(D) Dê cà ri</p>', 'A. Red snapper', 'B. Oxtail soup', 'C. Jerk chicken', 'D. Curried goat', '2025-04-28 23:45:18', '2025-05-02 01:00:50', 180);
INSERT INTO `questions` VALUES (1196, 'Peterkin\'s visit to Orange Bay Kitchen?', 'A', '<p><span style=\"color: #0033FF;\">193. Điều gì được gợi ý về chuyến thăm của cô Peterkin tới Orange Bay Kitchen? </span><br><span style=\"color: #0033FF;\"> (A) Cô ấy đã ở đó vào thứ Sáu. </span><br>(B) Cô ấy ăn tối một mình.<br>(C) Cô ấy yêu cầu thêm cơm.<br>(D) Cô ấy gọi món tráng miệng.</p>', 'A. She was there on a Friday.', 'B. She dined alone.', 'C. She requested extra rice.', 'D. She ordered dessert.', '2025-04-28 23:45:18', '2025-05-02 01:00:50', 180);
