INSERT INTO `questions` VALUES (1239, 'What problem does the woman mention?', 'C', '<p>36. Người phụ nữ đề cập đến vấn đề gì?<br>(A) Một báo cáo chưa được gửi.<br>(B) Hóa đơn không chính xác.<br><span><strong>(C) Một chính sách đã không được tuân theo.</strong></span><br>(D) Đơn hàng chưa được giao.</p>', 'A. A report has not been submitted.', 'B. An invoice is not accurate.', 'C. A policy has not been followed.', 'D. An order has not been delivered.', '2025-04-28 23:49:02', '2025-05-02 01:00:50', 214);
INSERT INTO `questions` VALUES (1240, 'What does the man say he will do?', 'B', '<p>37. Người đàn ông nói anh ta sẽ làm gì?<br>(A) Xóa một tập tin điện tử<br><span><strong>(B) Cho phép hoàn trả chi phí</strong></span><br>(C) Tổ chức một cuộc họp bán hàng<br>(D) Xem lại bảng tính</p>', 'A. Delete an electronic file', 'B. Authorize a reimbursement', 'C. Set up a sales meeting', 'D. Review a spreadsheet', '2025-04-28 23:49:02', '2025-05-02 01:00:50', 214);
