INSERT INTO `questions` VALUES (1502, 'At what depth should samples be collected this month?', 'A', '<p>99. Nhìn vào đồ họa. Mẫu nên được thu thập ở độ sâu nào trong tháng này?<br><span><strong>(A) 12 inch</strong></span><br>(B) 4 inch<br>(C) 6 inch<br>(D) 8 inch</p>', 'A. 12 inches', 'B. 4 inches', 'C. 6 inches', 'D. 8 inches', '2025-04-29 00:05:40', '2025-05-02 01:00:50', 308);
