INSERT INTO `questions` VALUES (1800, 'What is stated about large bags?', '', '<p><span style=\"color: red;\">159. Người ta nói gì về túi lớn? </span><br><span style=\"color: red;\"> (A) Chúng có thể được đặt trong hộp có khóa với một khoản phí. </span><br>(B) Chúng phải được để bên ngoài tòa nhà.<br>(C) Họ sẽ được một người phục vụ kiểm tra.<br>(D) Chúng phải được cất giữ dưới ghế ngồi.</p>', 'A. They can be put in a locked box for a fee.', 'B. They must be left outside the building.', 'C. They will be inspected by an attendant.', 'D. They must be stored under a seat.', '2025-04-29 14:01:31', '2025-05-02 01:00:50', 411);
